.unique-header {
    position  : fixed;
    height    : 80px;
    width     : 100%;
    z-index   : 100;
    padding   : 0 20px;
    background: #303b97;
    /* Ensure a background color if needed */
}
        
        .centered-content {
            display: flex;
            justify-content: center;
            align-items: center;
            width: 100%;
            height: 100vh;
        }
        
        .container {
    max-width: 2000px; /* Increased maximum width */
    padding: 40px; /* Increased padding */
    background-color: rgba(255, 255, 255, 0.7); /* Set opacity to 70% */
    border-radius: 12px; /* Increased border radius */
    box-shadow: 0 0 20px rgba(0, 0, 0, 0.2); /* Adjusted box shadow */
}


        h1 {
            text-align: center;
            color: #333;
        }

        .info {
            max-width: 2000px; /* Increased maximum width */
    padding: 40px; /* Increased padding */
    background-color: rgba(255, 255, 255, 0.7); /* Set opacity to 70% */
    border-radius: 12px; /* Increased border radius */
    box-shadow: 0 0 20px rgba(0, 0, 0, 0.2); /* Adjusted box shadow */
        }

        .info p {
            margin-bottom: 10px;
        }

        .info p:last-child {
            margin-bottom: 0;
        }

        .info p strong {
            color: #555;
        }

        .info p a {
            color: #007bff;
            text-decoration: none;
        }

        .info p a:hover {
            text-decoration: underline;
        }
        .black-navbar #header #navbar a,
      .black-navbar #header #navbar a:focus {
        color: #000; /* Set the desired text color */
      }

     
